SELECT a.irowno AS orderNumber, a.inatunitprice as taxPrice, a.iquantity as fquantity, a.cinvcode, a.cordercode as orderDetailCode
FROM SaleOrderSQ as a right join (SELECT distinct(SaleOrderQ.id) as soi FROM SaleOrderQ LEFT JOIN (SELECT id AS kkid FROM so_somain_extradefine) sse ON kkid = saleorderq.id INNER JOIN SaleOrderSQ ON SaleOrderQ.id = SaleOrderSQ.id LEFT JOIN (SELECT isosid AS kbsei FROM so_sodetails_extradefine) ssef ON kbsei = saleordersq.isosid WHERE cSOCode in ( SELECT distinct(SaleBillVouchZW.csocode) AS orderDetailCode FROM SaleBillVouchZT INNER JOIN SaleBillVouchZW ON SaleBillVouchZT.sbvid = SaleBillVouchZW.sbvid WHERE (cVouchType = 26 OR cVouchType = 27) AND (ddate >= #{startDate}) AND (ddate <= #{endDate}) AND ISNULL (cchildcode, '') = ''
)) as b on a.id=b.soi

