select ChannelCode, sum(Amount) as amount
from t_pay_order
where



    and PaySuccTime between #{paySuccTimeStampStart,jdbcType=BIGINT} and #{paySuccTimeStampEnd,jdbcType=BIGINT}


    and PaySuccTime >= #{paySuccTimeStampStart,jdbcType=BIGINT}




    and PaySuccTime <= #{paySuccTimeStampEnd,jdbcType=BIGINT}



  and Status between 2 and 3


  and MchId like #{mchId,jdbcType=VARCHAR}


  and Currency = #{currency,jdbcType=VARCHAR}


  and ChannelCode = #{channelCode,jdbcType=TINYINT}


group by ChannelCode

select ChannelCode, ChannelId, PayOrderId
from t_pay_order
where



      and PaySuccTime between #{paySuccTimeStampStart,jdbcType=BIGINT} and #{paySuccTimeStampEnd,jdbcType=BIGINT}


      and PaySuccTime >= #{paySuccTimeStampStart,jdbcType=BIGINT}




      and PaySuccTime <= #{paySuccTimeStampEnd,jdbcType=BIGINT}



    and Status between 2 and 3


    and MchId like #{mchId,jdbcType=VARCHAR}


    and Currency = #{currency,jdbcType=VARCHAR}


    and ChannelCode = #{channelCode,jdbcType=TINYINT}
